//!exec

//通过 //!exec 声明为执行逻辑
//通过 global 访问全局对象
//通过 this.send 发送数据
//通过 this.params 访问路由参数
//通过 this.body 访问请求主体
//通过 this.query 访问查询字符串

let item = this.body;
item.name = this.params.name;
global.list.push(item);
this.send({ success: true });

//!exec

//通过 //!exec 声明为执行逻辑
//通过 global 访问全局对象
//通过 this.send 发送数据
//通过 this.params 访问路由参数
//通过 this.body 访问请求主体
//通过 this.query 访问查询字符串

let index = global.list.findIndex(item => item.name == this.params.name);
global.list.splice(index, 1);
this.send({ success: true });

//!exec

//通过 //!exec 声明为执行逻辑
//通过 global 访问全局对象
//通过 this.send 发送数据
//通过 this.params 访问路由参数
//通过 this.body 访问请求主体
//通过 this.query 访问查询字符串

let item = global.list.find(item => item.name == this.params.name);
this.send({ success: true, item: item });
